  return (
    <div className="Recommended">
      <button>{"<"}</button>
      {products.map((product, index) => (
        <div key={index} id ={index} onClick={(e) => {
          var index = e.target.id
        }}>
          <img className="thumbnail"src={product.image[0]}/>
          <p>{product.name}</p>
        </div>
      ))}
      <button>{">"}</button>
    </div>
  );
}

export default Recommended;
